import React from "react";

import style from '../Pagination/Pagination.module.css';

export default function Pagination ({allCountry,countriesPerPage, paginado}){

	const numberpage=[];

	//Dividimos la cantidad total de paises por el nro de paises por pagina y pusheamos 10 por pagina
	for(var i=1; i<=Math.ceil(allCountry/countriesPerPage); i++){
		numberpage.push(i);
	}
    //debo retornas el componente(nav), cada li va a ser un boton (onClick) a traves del cual accedemos a cada pagina clickeada
	return (
	<nav className={style.paginado}>
		<ul className={style.paginado}>
			{numberpage &&
				numberpage.map(number=>(
					<li key={number}>
					<button className={style.buttonPag} 
					onClick={() => paginado(number)}>{number}</button>
					</li>
			))}
		</ul>
	</nav>
	)
}
