import {GET_ACTIVITY, GET_DETAIL, GET_COUNTRY,SEARCH_COUNTRY, FILTER_ACTIVITY, FILTER_ORDER_AZ, FILTER_ORDER_POPU, FILTER_CONTINENT} from '../actions/actionTypes';


import axios from 'axios'


export function getCountry(){
	return async (dispatch)=>{
		var json= await axios.get('http://localhost:3001/countries');
		console.log(json.data);
		return dispatch({
			type: GET_COUNTRY,
			payload: json.data
		})
	}
};

export function searchCountry(value){
	// return async (dispatch)=>{
	// 	var search = await axios.get('http://localhost:3001/countries?name='+value);
	// 	return dispatch({
	// 		type: SEARCH_COUNTRY,
	// 		payload: search.data
	// 	})
	// }

	return async function (dispatch) {
        try {
            var country = await axios.get(`http://localhost:3001/countries?name=${value}`)
            return dispatch ({
                type : "GET_SEARCH",
                payload : country.data
            })
        } 
        catch(error){
            console.log(error);
        }
    }
};

export function getCountriesDetail(id){
	return async (dispatch)=>{
		var detail = await axios.get('http://localhost:3001/countries/'+id);
		return dispatch({
			type:GET_DETAIL,
			payload:detail.data
		})
	} 
};

export function filterContinent(value){
	return{
		type:FILTER_CONTINENT,
		payload:value
	}
};

export function filterOrderAZ(value){
	return{
		type:FILTER_ORDER_AZ,
		payload:value
	}
};

export function filterOrderPOPU(value){
	return{
		type:FILTER_ORDER_POPU,
		payload:value
	}
};

export function addActivity(body){
	return async (dispatch)=>{
		var activity = await axios.post('http://localhost:3001/activity', body);
		return activity;
	}
};

export function getActivity(){
	return async (dispatch)=>{
		let get= await axios.get("http://localhost:3001/activity");
		return dispatch({
			type: GET_ACTIVITY,
			payload:get.data
		})
	}
};

export function filterActivity(value){
	return{
		type:FILTER_ACTIVITY,
		payload:value
	}
};
