import React from "react";

//Component Actions
import SearchBar from "../Actions/SearchBar";
import FilterContinent from "../Actions/FilterContinent";
import FilterByOrders from "../Actions/FilterByOrders";
import Reset from "../Actions/Reset";
import Activity from "../ActivityNav/Activity";

//STYLE
import style from '../Actions/Actions.module.css';

//Search actions
import { searchCountry } from '../../store/actions/actions';

export default function NavBar (props) {

  //Search
  const [search, setSearch]=useState("");
  const dispatch = useDispatch()

 function handleSearch(){
 dispatch(searchCountry(search))
 }

   function handleInput(e){
   e.preventDefault()
   setSearch(e.target.value); 
  }
         return(
            <div className={style.container}>

            <SearchBar 
            handleInput={handleInput}
            handleSearch={handleSearch}/>

            <FilterContinent/>       

            <FilterByOrders/>

            <Reset/>

            <Activity/>

      </div>

    )
};

//export default NavBar;
