import React, { useState } from'react';
import style from '../Actions/Actions.module.css';
import {filterContinent} from '../../store/actions/actions';

import {useDispatch} from 'react-redux';

import { getCountry } from '../../store/actions/actions';
import e from 'express';

export default function FilterContinent() {

  const [reset, inputReset] = useState();

   const dispatch = useDispatch()

    function handledFilter(e){
      e.preventDefault()
      dispatch(filterContinent(e.target.value))
    }

    function handleReset(e){
      dispatch(getCountry())
       inputReset({
        ...reset,
        [e.target.value]:e.target.value='all'
       })
  };

    return(
    <div>
    <div className={style.title} >Order by Continent</div>

    <select className={style.input} 
         onChange={event=>handledFilter(event)}>

      <option value ='all'> Show all </option>
      <option value='Africa'> Africa </option>
      <option value='Americas'> Americas </option>
      <option value='Antarctic'> Antarctic </option>
      <option value ='Asia'> Asia </option>
      <option value='Europe'> Europa </option>
      <option value='Oceania'> Oceania </option>
    </select>

    <div>
        <button className={style.button}
        onClick={event=>handleReset(event)} >
            Reset filters
        </button>
      </div>
  </div>

    )
};
