import React from "react";
import style from '../Actions/Actions.module.css';

//Action
import { filterOrderPOPU } from "../../store/actions/actions";
//Dispacth
import {useDispatch} from 'react-redux';

export default function FilterPopulations () {

	const dispatch=useDispatch()

	function handleFilter(e){
		e.preventDefault()
		dispatch(filterOrderPOPU(e.target.value))
	}

    return(

        <div>
        <div className={style.title} >Order by Population</div>
        <select className={style.input} 
        onChange ={event=> handleFilter(event)}>

          <option value="" >Choose order</option>
            <option value ='asc'>Smaller population </option>
            <option value ='des'>Largest population </option>
        </select>
      </div>
    )
}
