import React from "react";
import SearchBar from "../SearchBar/SearchBar";

//Componente Actions
import FilterContinent from "../Actions/FilterContinenet";

//LINK y STYLE
import { Link } from "react-router-dom";
import style from '../NavBar/NavBar.module.css';


//FALTA --> prueba6
    //ONCHANGE --> Filtrar por continente
    //ORDER by POPULATION
    //ALPHABETICAL ORDER
    //ONCLIKC de RESET todos los filtros

    // --> prueba4
    //Filtro CreateActivity y Select ALL Activities

export default function NavBar () {
         return(
            <div className={style.container}>

            <SearchBar/>

            <FilterContinent/>



            <div>
              <div className={style.title} >Order by Population</div>
              <select className={style.input} onChange =''>
                <option value="" >Choose order</option>
                  <option value ='asc' key='asc'>Lowest poppulation first</option>
                  <option value ='des' key='des'>Largest population first</option>
              </select>
            </div>



            <div>
              <div className={style.title} >Alphabetical Order</div>
              <select  className={style.input} onChange =''>
              <option value="" >Choose order</option>
              <option value ='asc' key='asc'>A-Z</option>
              <option value ='des' key='des'>Z-A</option>
              </select>
            </div>



            <div>
              <button onClick='submit' className={style.button}>Reset filters</button>
            </div>

{/*COMPONENTE CREATE - ACTIVITY & FILTER*/}

          <div className={style.activitycontainer}>
            <div className={style.titleAct}>Activities</div>
            <div >
             <p className={style.title}>
                {/*Create activities to filter*/}
             </p>

                 <select>
                    <option value = 'All'>Select activity</option>  
                </select>
            </div>


            <Link to = '/activity'><button className={style.button}>Create Activity</button></Link>
          </div>


            </div>

         )
};

//export default NavBar;
