import React from "react";

import style from '../Pagination/Pagination.module.css';

export default function Pagination ({allCountry,countriesPerPage, paying, currentPage, setCurrentPage}){

	const numberpage=[];


	//Dividir la cantidad total por el nro de paises por pagina 
	for(var i=1; i<=Math.ceil(allCountry/countriesPerPage); i++){
		numberpage.push(i);
	}
    //retornar el componente nav (nav=paginacion). Generamos todas las paginas y cada items va a contener un boton (onClick) a traves del cual voy a acceder a la pagina clickeada. 

//Previus Page
const onPreviusPage=()=>{setCurrentPage(currentPage-1)}

//Next Page
const onNextPage =()=>{setCurrentPage(currentPage+1)}

	return (
<div>
    <div className={style.containPreviusNext}>
       <button className={style.previusNext} 
	   onClick={onPreviusPage}
	   {...currentPage==1 ? style.invalid : ''}>Previus 
	   </button>

	   <button className={style.previusNext}
	   onClick={onNextPage}
	   {...currentPage>=numberpage.length ? style.invalid : ''}>Next 
	   </button>
    </div>

	<nav className={style.paginado}>
		<ul className={style.paginado}>
			{numberpage &&
				numberpage.map(number=>(
					<li key={number}>
					<button className={style.buttonPag} 
					onClick={() => paying(number)}>{number}</button>
					</li>
			))}
		</ul>
	</nav>
</div>		
	)
}
