import React from "react";
import style from '../Actions/Actions.module.css';

//Action
import { filterOrderAZ } from "../../store/actions/actions";

//Dispacth
import { useDispatch } from "react-redux";

export default function FilterOrderAlphabetical () {

    const dispatch=useDispatch()


	function handleFilter(e){
		e.preventDefault()
		dispatch(filterOrderAZ(e.target.value))
	}

    return(

     <div>
        <div className={style.title} >Alphabetical Order</div>

        <select  className={style.input} 
        onChange ={event=>handleFilter(event)}>
        <option value="" >Choose order</option>
        <option value ='asc' key='asc'>A-Z</option>
        <option value ='des' key='des'>Z-A</option>
        </select>
      </div>


    )
}
