import {React, useState }from "react";
import { filterOrder } from "../../store/actions/actions"; 
import {useDispatch, useSelector} from "react-redux";

import style from './Actions.module.css';

import Pagination from "../Pagination/Pagination";
import Card from "../CardsComponent/Card";
import styleCards from '../CardsComponent/Cards.module.css';

export default function FilterByOrders(){
	const allCountry= useSelector((state)=>state.countries)  // me traigo todo los paises
	const dispatch = useDispatch()

	const[currentPage, setCurrentPage]=useState(1);
	const[countriesPerPage, setCountriesPerPage]=useState(9);
	const indexOfLastCountries = currentPage * countriesPerPage;
	const indexOfFirstCountries = indexOfLastCountries - countriesPerPage;
	const currentCountries = allCountry.slice(indexOfFirstCountries,indexOfLastCountries);

	const paginado=(pageNumber)=>{
		setCurrentPage(pageNumber)
	}
	function handel(e){
		e.preventDefault()
		dispatch(filterOrder(e.target.value))
	}

	return (
         <div>
        <div>

		<Pagination allCountry={allCountry.length} countriesPerPage={countriesPerPage} paginado={paginado}/>

        </div>

		<div className={style.containerCard}>

            {currentCountries.map((e)=>{
				return <Card key={e.id} id={e.id} name={e.name} flag={e.flag} continent={e.continent}/>
			})}
		</div>



    <div>  
         <div className={style.title} >Filters</div>
        <select className={style.input}
        onChange={e=>handel(e)} >
            <option value="all">Order By</option>
            <option value="asc">Ascendant</option>
            <option value="desc">Descending</option>
            <option value="min">Minimum Population</option>
            <option value="max">Maximum Population</option>
        </select>
    </div>
    </div>
       )
}
