import React from "react";
import {useDispatch, useSelector} from 'react-redux'; 
import { useEffect, useState } from "react";

//REDUX/Store/Actiones {getCountry, addActivity}
import {getCountry, addActivity} from '../../store/actions/actions';

//LINK
import {Link} from 'react-router-dom';

//Validations Form
import validate from '../../utils/validate';

//Components
import Header from '../../components/Header/Header';

//Style
import style from '../Form/CreateActivity.module.css';

export default function CreateActivity () {
        //dispacth
        const dispatch = useDispatch();
        //selector
        const country = useSelector((state)=>state.countries);
        //state
        const [inputBody, setInputBody] = useState({
          name:'',
          difficulty:"",
          duration:"",
          season:"",
          countries:[]
        });

        const [errors, setErrors] = useState({});



        //HandleInput NAME
        function handleInput(e){
          e.preventDefault()
          setInputBody({
            ...inputBody,
            [e.target.name]:e.target.value
          })
        };

        //Input select for DIFICULTY, DURATION, SEASON
        function handleDifficulty(e){
          e.preventDefault()
          setInputBody({
            ...inputBody,
            [e.target.name]:e.target.value
          })

          setErrors(validate({
            ...inputBody,
            [e.target.name]: e.target.value
          }));
        };

        function handleDuration(e){
          e.preventDefault()
          setInputBody({
            ...inputBody,
            [e.target.name]:e.target.value
          })
        };

        function handleSeason(e){
          e.preventDefault()
          setInputBody({
            ...inputBody,
            [e.target.name]:e.target.value
          })
        };

        //Input select COUNTRY
      	function handleSelectCountry(e){
          e.preventDefault()
          setInputBody({
            ...inputBody,
            countries:[...inputBody.countries, e.target.value]
          })
        };


        //delete country Adds
        function handleDelete(e){
          setInputBody({
            ...inputBody,
            countries: inputBody.countries.filter(r=> r!==e)
          })	
        }

        //useEffect for getCountry()
        useEffect(()=>{
          dispatch(getCountry())
        },[dispatch]);


  //handleSubmit FORM
        function handleSubmit(e){
          e.preventDefault()
          dispatch(addActivity(inputBody))
          alert("activity created")

        };
    return (

  <div className={style.container}>
        <Header/>

        <div className={style.createContainer}>  

         <div>
{/*FORM */}
          <form className={style.formContainer} onSubmit={event=>handleSubmit(event)}>
                  <p>Create Activity</p>

 {/*NAME*/}                 
      <div>
            <label className={style.atribute}> Activity Name: </label>

            <input className={style.input} 
            type = 'text' 
            value = {inputBody.name} 
            name ='name'
            onChange={event=>handleInput(event)}
            required>
            </input>      
      </div>

{/*DIFICULTY*/}   
      <div>
            <label className={style.atribute}>Difficulty: </label>

            {errors.difficulty && (<p className={style.pdanger}>{errors.difficulty}</p>)}

            <select className={style.select}
            name="difficulty" 
            onChange={event=>handleDifficulty(event)}>
            <option></option>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
            </select>
			</div>

 {/*DURATION*/}    
        <div>
              <label className={style.atribute}>Duration: </label>
              <select className={style.select}
              name="duration" 
              onChange={event=>handleDuration(event)}>
              <option>-</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
              <option value="6">6</option>
              <option value="7">7</option>
              <option value="8">8</option>
              <option value="9">9</option>
              <option value="10">10</option>
              <option value="11">11</option>
              <option value="12">12</option>
              <option value="13">13</option>
              <option value="14">14</option>
              <option value="15">15</option>
              <option value="16">16</option>
              <option value="17">17</option>
              <option value="18">18</option>
              <option value="19">19</option>
              <option value="20">20</option>
              <option value="21">21</option>
              <option value="22">22</option>
              <option value="23">23</option>
              <option value="24">24</option>
              </select>
		    </div>


 {/*SEASON*/}  
        <div>

            <label className={style.atribute}>Season: </label>

            <select className={style.select}
            name="season" 
            onChange={event=>handleSeason(event)}>
            <option>-</option>
            <option value="summer">summer</option>
            <option value="autumn">autumn</option>
            <option value="winter">winter</option>
            <option value="spring">spring</option>
            </select>

        </div>

 {/*COUNTRIES*/}  
        <div>
            <label className={style.atribute}>Countries: </label>
            <select className={style.select}

            onChange= {event=>handleSelectCountry(event)}>
            <option>-</option>

             {country.map((e)=>(
              <option value={e.id}>{e.name}</option>))
            } 
            </select>
			  </div>


{/*DESDE AQUI LOS BOTONES*/}

    {/*BACK*/}
        <div className={style.buttonscontainer}>
              <Link to = '/home'><button className={style.button}>Back</button>
              </Link>

    {/*ADD*/}   
              <button className={style.button} 
              type='submit' >
                   Add Activity
              </button>
        </div>


 {/*BUTTON COUNTRIES ADD */}           
            <div className={style.countriesADDcontainer}>

                 {inputBody.countries.map(e=>
              <div>
                  <p>{e}</p>
                  <button className={style.buttonXdelete}
                  onClick={()=>handleDelete(e)}
                  >
                    x
                  </button>
              </div>
              )}

            </div> 

          </form>

        </div>{/*cierre div form*/}

      </div>{/*cierre div cardContainer*/}

  </div>

  );
};


//export default CreateActivity;
