import {React, useState} from "react";
import {useDispatch} from 'react-redux';

//Component Actions
import SearchBar from "../Actions/SearchBar";
import FilterContinent from "../Actions/FilterContinent";
import FilterByOrders from "../Actions/FilterByOrders";
import Reset from "../Actions/Reset";
import Activity from "../ActivityNav/Activity";

  //FilterContinent
  import {filterContinent} from '../../store/actions/actions';
  //Order
  import { filterOrder } from "../../store/actions/actions"; 
  //reset
  import { getCountry } from '../../store/actions/actions';


//STYLE
import style from '../Actions/Actions.module.css';

export default function NavBar (props) {

  const dispatch = useDispatch()

  const [order, setOrder] = useState('')

  //filter continent
  function handledFilter(e){
    e.preventDefault()
    dispatch(filterContinent(e.target.value))

  };

  //filter Order
  function handleByOrder(e){
		e.preventDefault()
		dispatch(filterOrder(e.target.value))
    setOrder({
      ...order,
      [e.target.name]:e.target.value
    })
	};

  //reset
  function handleReset(e){
    dispatch(getCountry())

    setOrder({
      value:'all',

    })
};


         return(
            <div className={style.container}>

            <SearchBar/>

            <FilterContinent handledFilter={handledFilter}/>       

            <FilterByOrders handleByOrder= {handleByOrder}/>

            <Reset handleReset={handleReset}/>

            <Activity/>

      </div>

    )
};

