import {React, useState} from 'react';
import {useDispatch} from "react-redux";
//Action
import { searchCountry } from '../../store/actions/actions';

import style from './SearchBar.module.css';

export default function SearchBar () {


         //useSelector me traigo las countries del store
	const allCountry= useSelector((state)=>state.countries)  
     const [search, setSearch]=useState("");
 	   const dispatch = useDispatch()

	  function handleSearch(){

       if(search.length===0){
        alert('ingrese al menos un caracter')
      }else{
        dispatch(searchCountry(search))

        if(allCountries.length === 0){
          alert('No se existen paises para los datos ingresados')
        }
      }
   }

      function handleInput(e){
      //e.preventDefault()
      setSearch(e.target.value);
     }

    return(
       <div>juan
              <input className={style.input} 
              type = 'text' 
              onChange= {event=>handleInput(event)}
              placeholder = 'Search country...'  
              />

             <button title='search' className={style.button}
             onClick={()=>handleSearch()}>
                Search
            </button>

          </div>

    )
};

