import React from "react";
import { Link } from 'react-router-dom';

//style idem component Detail
import style from '../../views/Detail/Detail.module.css';

export default function CardActivityDetail ({activities}){

    if (activities && activities.length > 0) {
        return (
            <div className={style.container}>

                <h2>-----------------------</h2>
                <h3>Activities for Country:</h3>
                <div className={style.detailContainer}>
                    {activities && activities.map((e) => (
                        <ul key={e.id} >
                            <li>Name: {e.name}</li>
                            <li>Duration (hour): {e.duration}</li>
                            <li>Difficulty: {e.difficulty}</li>
                            <li>Season: {e.season}</li>
                            <li>-----------</li>
                        </ul>
                    ))}
                </div>

                <div className={style.buttonscontainer}>
              <Link to = '/home'>
                <button className={style.button}>
                    Back
                </button>
              </Link>
              </div>
            </div>


        )
    } else {
        return <h3 className={style.noActivity}>No activities were created this country<Link  to='/activity'><p> Click here to add </p></Link></h3>
    }
}
