import React from "react";
import Card from "./Card"
import {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";

import { useMyContext } from "../../provider/provider"; 

//Action
import {getCountry} from '../../store/actions/actions';

//Pagination
import Pagination from '../Pagination/Pagination';

//Style
import style from './Cards.module.css';


export default function Cards(){

	const allCountry= useSelector((state)=>state.countries)  // me traigo todo los paises
	const dispatch = useDispatch()

	const[state, setState]=useMyContext(1)

	const [order, setOrder] = useMyContext('');
	const countriesPerPage = 10;

	const lastCountries = currentPage * countriesPerPage;
	const firstCountries = lastCountries - countriesPerPage;
	const currentCountries = allCountry.slice(firstCountries,lastCountries);

	const paginado=(pageNumber)=>{
		setState(pageNumber)
	}

	useEffect(()=>{
		dispatch(getCountry())
	},[dispatch])


	return <div>

		<Pagination allCountry={allCountry.length} countriesPerPage={countriesPerPage} paginado={paginado}/>

		<div className={style.containerCard}>

            {currentCountries.map((e)=>{
				return <Card key={e.id} id={e.id} name={e.name} flag={e.flag} continent={e.continent}/>
			})}
		</div>

	</div>
}
