const validLetterAndNumbers = new RegExp(/^(?=.*[a-z])(?=.*[0-9])/)

export default function validate(input) {

    let errors = {};

    if(!input.name){
      errors.name ="Name is required"

    } else if(validLetterAndNumbers.test(!input.difficulty)){
      errors.difficulty ="Dificulty is required"

    } else if(validLetterAndNumbers.test(!input.season)){
      errors.season ="Please select season"

    } else if(validLetterAndNumbers.test (input.countries.length < 1)){
      errors.countries ="Please select the countries where this activity is practiced"
    } 

    return errors;
  }
